import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

function NavBar() {
  const [menuOpen, setMenuOpen] = useState(false);

  function toggleMenu() {
    setMenuOpen(!menuOpen);
  }

  return (
    <nav className="bg-cyan-400 text-white font-custom py-4">
      <div className="md:hidden">
        <button
          type="button"
          className="text-gray-300 hover:text-white focus:outline-none focus:text-white"
          aria-label="Toggle menu"
          onClick={toggleMenu}
        >
          <svg className="h-6 w-6 fill-current" viewBox="0 0 24 24">
            <path
              className={menuOpen ? 'hidden' : 'block'}
              fillRule="evenodd"
              clipRule="evenodd"
              d="M4 6H20V8H4V6ZM4 11H20V13H4V11ZM4 16H20V18H4V16Z"
            />
            <path
              className={menuOpen ? 'block' : 'hidden'}
              fillRule="evenodd"
              clipRule="evenodd"
              d="M4 6H20V8H4V6ZM4 11H20V13H4V11ZM4 16H20V18H4V16ZM4 21H20V23H4V21Z"
            />
          </svg>
        </button>
      </div>
      <div className="container mx-auto">
        <ul className={`flex space-x-4 ${menuOpen ? 'md:hidden block bg-black' : 'md:hidden hidden bg-black'}`}>
          <li><NavLink to="/" className="hover:text-gray-300">Home</NavLink></li>
          <li><NavLink to="/about" className="hover:text-gray-300">About</NavLink></li>
          <li><NavLink to="/artists" className="hover:text-gray-300">Artists</NavLink></li>
          <li><NavLink to="/booking" className="hover:text-gray-300">Booking</NavLink></li>
        </ul>
      </div>
    </nav>
  );
}

export default NavBar;
